import React from "react";
import { View, StyleSheet, Text } from "react-native";
import { DrawerContentScrollView, DrawerItem } from "@react-navigation/drawer";
import { Avatar, Title } from "react-native-paper";
import { TouchableOpacity } from "react-native";
// import Icon from 'react-native-vector-icons/MaterialCommunityIcons';
// import Icon from 'react-native-vector-icons/MaterialIcons';
import Icon from "react-native-vector-icons/MaterialCommunityIcons";
import { useNavigation } from "@react-navigation/native";
import theme from "../theme";

const DrawerList = [
  // {icon: 'account-multiple', label: 'Profile', navigateTo: 'Profile'},
  // {icon: 'account-group', label: 'Balance', navigateTo: 'Balance'},
  // {icon: 'settings', label: 'Settings', navigateTo: 'Settings'},
  { icon: "account-circle", label: "Profile", navigateTo: "Profile" },
  { icon: "wallet", label: "Balance", navigateTo: "Balance" },
  { icon: "cog", label: "Settings", navigateTo: "Settings" },
];

const DrawerLayout = ({ icon, label, navigateTo }) => {
  const navigation = useNavigation();

  return (
    <DrawerItem
      icon={({ color, size }) => (
        <Icon name={icon} color={theme.colors.white} size={size} />
      )}
      label={label}
      labelStyle={{ color: theme.colors.white }}
      onPress={() => {
        navigation.navigate(navigateTo);
      }}
    />
  );
};

const DrawerItems = (props) => {
  return DrawerList.map((el, i) => {
    return (
      <DrawerLayout
        key={i}
        icon={el.icon}
        label={el.label}
        navigateTo={el.navigateTo}
      />
    );
  });
};
export default function DrawerContent(props) {
  return (
    <View style={{ flex: 1 }}>
      <DrawerContentScrollView {...props}>
        <View style={StyleSheet.drawerContent}>
          <TouchableOpacity activeOpacity={0.8}>
            <View style={styles.userInfoSection}>
              <View style={{ flexDirection: "row", marginTop: 15 }}>
                <Avatar.Image
                  source={{
                    uri: "data:image/png;base64,",
                  }}
                  size={50}
                  style={{ marginTop: 5 }}
                />
                <View style={{ marginLeft: 10, flexDirection: "column" }}>
                  <Title style={styles.title}>Ahmed Othman</Title>
                  <Text style={styles.caption} numberOfLines={1}>
                    {/* adarshthauk21@gmail.com */}
                    Freelancer
                  </Text>
                </View>
              </View>
            </View>
          </TouchableOpacity>
          <View style={styles.drawerSection}>
            <DrawerItems />
          </View>
        </View>
      </DrawerContentScrollView>
      <View style={styles.bottomDrawerSection}>
        <DrawerItem
          icon={({ color, size }) => (
            <Icon name="exit-to-app" color={theme.colors.white} size={size} />
          )}
          label="Log Out"
          labelStyle={{ color: theme.colors.white }}
        />
      </View>
    </View>
  );
}

const styles = StyleSheet.create({
  drawerContent: {
    flex: 1,
  },
  userInfoSection: {
    paddingLeft: 20,
  },
  title: {
    fontSize: 16,
    marginTop: 3,
    fontWeight: "bold",
    color: theme.colors.white,
  },
  caption: {
    fontSize: 13,
    lineHeight: 14,
    color: theme.colors.ternaryDark,

    width: "100%",
  },
  row: {
    marginTop: 20,
    flexDirection: "row",
    alignItems: "center",
  },
  section: {
    flexDirection: "row",
    alignItems: "center",
    // marginRight: 15,
  },
  paragraph: {
    fontWeight: "bold",
    marginRight: 3,
  },
  drawerSection: {
    marginTop: 15,
    borderBottomWidth: 0,
    borderBottomColor: theme.colors.ternaryLight,
    borderBottomWidth: 1,
    marginLeft: 10,
  },
  bottomDrawerSection: {
    marginBottom: 15,
    borderTopColor:  theme.colors.ternaryLight,
    borderTopWidth: 1,
    borderBottomColor:  theme.colors.ternaryLight,
    borderBottomWidth: 1,
    marginLeft: 10,
  },
  preference: {
    flexDirection: "row",
    justifyContent: "space-between",
    paddingVertical: 12,
    paddingHorizontal: 16,
  },
});
